-- VITAEOLOGY v2 - ESERCIZI Q1 (Settimane 1-13)
-- Tipo: RICONOSCIMENTO - Solo osservazione

-- Settimana 1: Autoconsapevolezza
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (1, 'Lo Specchio del Leader', 'Autoconsapevolezza', 'autoconsapevolezza', 'Vision', 'riconoscimento', 'base', 'Q1', 25, 'Ogni giorno prendi decisioni che rivelano chi sei come leader. Questa settimana osserverai quei momenti in cui ti vedi chiaramente mentre operi.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "Il leader che si osserva", "prompt": "Ricorda un momento recente in cui ti sei fermato a riflettere su come stavi gestendo una situazione.", "instructions": "Pensa all''ultima settimana. Cerca un momento in cui hai notato te stesso mentre lavoravi."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "I segnali dell''autoconsapevolezza", "prompt": "Cosa ti ha fatto fermare e osservarti?", "guiding_questions": ["Cosa stava succedendo intorno a te?", "Che emozione hai notato in te stesso?", "Cosa hai fatto dopo esserti osservato?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Momenti di chiarezza", "prompt": "Questa settimana, nota semplicemente quando ti osservi mentre operi.", "action_steps": ["Ogni sera, chiediti: mi sono osservato oggi?", "Se si, annotalo brevemente", "Non giudicare, solo osserva"]}'::jsonb, 'Un breve diario di 3-5 momenti in cui ti sei osservato durante la settimana', ARRAY['Quando mi osservo, cosa noto di piu?', 'In quali situazioni mi viene naturale fermarmi?', 'Come mi sento dopo essermi osservato?'], 'Va bene se questa settimana non hai notato molti momenti. L''autoconsapevolezza e una capacita che gia possiedi.');

-- Settimana 2: Coraggio
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (2, 'Piccoli Atti di Coraggio', 'Coraggio', 'coraggio', 'Vision', 'riconoscimento', 'base', 'Q1', 25, 'Il coraggio non e assenza di paura, ma agire nonostante essa. Questa settimana osserverai dove il coraggio gia opera nella tua vita quotidiana.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che agisce nonostante l''incertezza", "prompt": "Ricorda un momento recente in cui hai fatto qualcosa pur non essendo sicuro del risultato.", "instructions": "Non cercare gesti eroici. Cerca momenti normali: una telefonata difficile, una decisione presa senza avere tutti i dati."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come si manifesta il tuo coraggio", "prompt": "Cosa ti ha spinto ad agire in quel momento?", "guiding_questions": ["Cosa rischiavi in quella situazione?", "Cosa ti dicevi mentre agivi?", "Come ti sei sentito dopo?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Riconoscere il coraggio quotidiano", "prompt": "Questa settimana, nota quando agisci nonostante l''incertezza.", "action_steps": ["Ogni sera ripensa alla giornata", "Cerca un momento in cui hai agito senza certezza", "Annotalo senza giudizio"]}'::jsonb, 'Lista di 3-5 piccoli atti di coraggio osservati durante la settimana', ARRAY['Il coraggio e piu presente di quanto pensassi?', 'In quali ambiti mi viene piu naturale?', 'Cosa rende alcuni atti piu facili di altri?'], 'Se non hai notato atti di coraggio questa settimana, e normale. Spesso il coraggio opera cosi naturalmente che non lo riconosciamo.');

-- Settimana 3: Visione
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (3, 'Oltre l''Orizzonte', 'Visione', 'visione', 'Vision', 'riconoscimento', 'base', 'Q1', 25, 'Ogni imprenditore guarda oltre il presente. Questa settimana osserverai quando il tuo sguardo si allunga naturalmente verso il futuro.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che vede oltre l''immediato", "prompt": "Ricorda un momento in cui hai pensato a dove sara la tua attivita tra 1, 3 o 5 anni.", "instructions": "Cerca momenti in cui hai guardato avanti: pianificando, immaginando, anticipando."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come emerge la tua visione", "prompt": "Cosa ti ha fatto pensare al futuro in quel momento?", "guiding_questions": ["Eri solo o con altri?", "Cosa vedevi nel futuro?", "Come ti faceva sentire quel pensiero?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare lo sguardo lungo", "prompt": "Questa settimana, nota quando il tuo pensiero va oltre l''immediato.", "action_steps": ["Osserva quando pensi al futuro", "Nota se e spontaneo o provocato", "Annota brevemente cosa vedevi"]}'::jsonb, 'Appunti su 3-5 momenti in cui hai pensato al futuro', ARRAY['Quando mi viene naturale pensare al futuro?', 'Cosa vedo piu facilmente: opportunita o rischi?', 'Il futuro mi attrae o mi preoccupa?'], 'Se questa settimana sei stato assorbito dal presente, e comprensibile. La visione emerge quando c''e spazio.');

-- Settimana 4: Pensiero Strategico
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (4, 'Connessioni Invisibili', 'Pensiero Strategico', 'pensiero-strategico', 'Vision', 'riconoscimento', 'base', 'Q1', 25, 'La tua mente connette costantemente cause ed effetti. Questa settimana osserverai quando questo pensiero strategico emerge naturalmente.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La mente che connette i punti", "prompt": "Ricorda un momento in cui hai collegato due eventi apparentemente separati.", "instructions": "Cerca momenti in cui hai detto o pensato: se facciamo A, succedera B."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come ragioni strategicamente", "prompt": "Come hai fatto quel collegamento?", "guiding_questions": ["E stato un ragionamento o un''intuizione?", "Avevi informazioni o hai dedotto?", "Il collegamento si e rivelato corretto?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare le connessioni", "prompt": "Questa settimana, nota quando la tua mente connette cause ed effetti.", "action_steps": ["Osserva quando fai previsioni", "Nota quando spieghi perche qualcosa e successo", "Annota le connessioni che fai"]}'::jsonb, 'Lista di 3-5 connessioni causa-effetto notate durante la settimana', ARRAY['Le mie connessioni sono piu spesso giuste o sbagliate?', 'Ragiono meglio sul passato o sul futuro?', 'Quando la mia mente connette piu facilmente?'], 'Se non hai notato connessioni esplicite, prova a pensare alle decisioni che hai preso.');

-- Settimana 5: Empatia
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (5, 'Sentire l''Altro', 'Empatia', 'empatia', 'Relations', 'riconoscimento', 'base', 'Q1', 25, 'Ogni giorno percepisci le emozioni di chi ti circonda. Questa settimana osserverai quando questa capacita opera naturalmente.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che sente cio che l''altro sente", "prompt": "Ricorda un momento in cui hai capito come si sentiva qualcuno senza che te lo dicesse.", "instructions": "Cerca momenti in cui hai percepito tensione, entusiasmo, preoccupazione in un collaboratore."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come percepisci le emozioni", "prompt": "Come hai capito cosa sentiva l''altro?", "guiding_questions": ["Cosa ti ha fatto capire il suo stato d''animo?", "Era il tono, il corpo, le parole?", "Cosa hai fatto con quella percezione?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare la percezione emotiva", "prompt": "Questa settimana, nota quando senti le emozioni degli altri.", "action_steps": ["Osserva le tue reazioni alle emozioni altrui", "Nota cosa ti fa capire come sta l''altro", "Non agire, solo osserva"]}'::jsonb, 'Appunti su 3-5 momenti di percezione emotiva', ARRAY['Con chi mi viene piu facile?', 'Quali emozioni percepisco meglio?', 'Come uso di solito questa percezione?'], 'L''empatia a volte opera cosi velocemente che non la notiamo.');

-- Settimana 6: Comunicazione
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (6, 'Creare Comprensione', 'Comunicazione', 'comunicazione', 'Relations', 'riconoscimento', 'base', 'Q1', 25, 'Comunichi costantemente, ma alcuni momenti creano vera comprensione. Questa settimana osserverai quando la tua comunicazione connette davvero.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che crea comprensione", "prompt": "Ricorda un momento in cui hai spiegato qualcosa e l''altro ha veramente capito.", "instructions": "Cerca momenti in cui hai visto la comprensione negli occhi dell''altro."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come crei comprensione", "prompt": "Cosa hai fatto per far capire l''altro?", "guiding_questions": ["Che parole hai usato?", "Hai usato esempi?", "Hai verificato la comprensione?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare la comprensione condivisa", "prompt": "Questa settimana, nota quando crei vera comprensione.", "action_steps": ["Osserva le tue comunicazioni importanti", "Nota quando l''altro capisce davvero", "Annota cosa ha funzionato"]}'::jsonb, 'Lista di 3-5 momenti di comunicazione efficace', ARRAY['Cosa funziona meglio quando comunico?', 'Con chi mi viene piu facile?', 'Quando la comunicazione non funziona, cosa manca?'], 'Se non hai notato momenti di connessione comunicativa, e normale.');

-- Settimana 7: Fiducia
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (7, 'Costruire Ponti', 'Fiducia', 'fiducia', 'Relations', 'riconoscimento', 'base', 'Q1', 25, 'La fiducia e il fondamento di ogni relazione professionale. Questa settimana osserverai dove la fiducia gia opera nelle tue relazioni.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che costruisce fiducia", "prompt": "Pensa a una relazione professionale dove c''e fiducia reciproca.", "instructions": "Scegli un collaboratore, cliente o partner di cui ti fidi e che si fida di te."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come costruisci fiducia", "prompt": "Cosa hai fatto che ha costruito quella fiducia?", "guiding_questions": ["Qual e stato il momento in cui la fiducia e cresciuta?", "Cosa hai fatto o detto?", "Quanto tempo ci e voluto?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare la fiducia in azione", "prompt": "Questa settimana, nota i momenti in cui la fiducia opera.", "action_steps": ["Osserva le interazioni con persone di cui ti fidi", "Nota cosa rende fluide quelle interazioni", "Annota le differenze"]}'::jsonb, 'Riflessione su 2-3 relazioni di fiducia e come si sono costruite', ARRAY['Cosa hanno in comune le relazioni dove c''e fiducia?', 'Quanto tempo ci vuole di solito?', 'Cosa rompe la fiducia piu facilmente?'], 'La fiducia si costruisce lentamente e spesso non la notiamo.');

-- Settimana 8: Ascolto
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (8, 'Il Dono dell''Ascolto', 'Ascolto', 'ascolto', 'Relations', 'riconoscimento', 'base', 'Q1', 25, 'Ascoltare veramente e raro e prezioso. Questa settimana osserverai quando la tua capacita di ascolto opera al meglio.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che ascolta prima di rispondere", "prompt": "Ricorda un momento in cui hai ascoltato qualcuno completamente.", "instructions": "Cerca momenti in cui eri totalmente presente per l''altro."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come ascolti veramente", "prompt": "Cosa facevi mentre ascoltavi?", "guiding_questions": ["Dove guardavi?", "Cosa facevi con il corpo?", "Cosa pensavi mentre l''altro parlava?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare l''ascolto autentico", "prompt": "Questa settimana, nota quando ascolti veramente.", "action_steps": ["Osserva te stesso durante le conversazioni", "Nota quando sei totalmente presente", "Nota quando la mente vaga"]}'::jsonb, 'Appunti su 3-5 momenti di ascolto autentico', ARRAY['Quando mi viene piu facile ascoltare davvero?', 'Cosa mi distrae dall''ascolto?', 'Come cambia la conversazione quando ascolto veramente?'], 'L''ascolto autentico richiede energia e presenza.');

-- Settimana 9: Decisionalita
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (9, 'L''Arte di Scegliere', 'Decisionalita', 'decisionalita', 'Action', 'riconoscimento', 'base', 'Q1', 25, 'Ogni giorno prendi decine di decisioni. Questa settimana osserverai come la tua capacita decisionale opera naturalmente.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che sceglie con chiarezza", "prompt": "Ricorda una decisione recente che hai preso velocemente e con sicurezza.", "instructions": "Non deve essere una grande decisione. Cerca momenti in cui hai scelto senza esitazione."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come decidi con chiarezza", "prompt": "Cosa ti ha permesso di decidere cosi velocemente?", "guiding_questions": ["Avevi tutte le informazioni?", "Cosa ti ha dato sicurezza?", "Hai avuto ripensamenti dopo?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare le decisioni fluide", "prompt": "Questa settimana, nota quando decidi con facilita.", "action_steps": ["Osserva le decisioni che prendi", "Nota quali sono facili e quali difficili", "Annota cosa rende alcune piu semplici"]}'::jsonb, 'Lista di 5-7 decisioni osservate, con nota su facilita/difficolta', ARRAY['Quali decisioni mi vengono naturali?', 'Cosa blocca le mie decisioni?', 'Velocita e qualita vanno insieme per me?'], 'Se hai notato piu indecisioni che decisioni, e informazione preziosa.');

-- Settimana 10: Responsabilita
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (10, 'Ownership', 'Responsabilita', 'responsabilita', 'Action', 'riconoscimento', 'base', 'Q1', 25, 'Assumersi responsabilita e il cuore della leadership. Questa settimana osserverai dove gia ti prendi ownership naturalmente.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che si assume ownership", "prompt": "Ricorda un momento in cui hai detto: questo e mio, me ne occupo io.", "instructions": "Cerca momenti in cui hai preso in carico qualcosa senza che nessuno te lo chiedesse."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come ti assumi responsabilita", "prompt": "Cosa ti ha spinto a prendere ownership?", "guiding_questions": ["Era necessario o volontario?", "Come ti sei sentito?", "Cosa e successo dopo?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare l''ownership naturale", "prompt": "Questa settimana, nota quando ti assumi responsabilita.", "action_steps": ["Osserva quando dici me ne occupo io", "Nota cosa ti spinge a farlo", "Nota anche quando eviti di farlo"]}'::jsonb, 'Lista di 3-5 momenti di ownership osservati', ARRAY['Di cosa mi prendo responsabilita facilmente?', 'Cosa evito di prendere in carico?', 'La responsabilita mi pesa o mi energizza?'], 'Se non hai notato momenti di ownership esplicita, pensa alle cose che porti avanti ogni giorno.');

-- Settimana 11: Valori
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (11, 'La Bussola Interiore', 'Valori', 'valori', 'Vision', 'riconoscimento', 'base', 'Q1', 25, 'I tuoi valori guidano ogni decisione, spesso senza che tu ne sia consapevole. Questa settimana osserverai quando i tuoi valori emergono nelle tue scelte.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che opera con integrita", "prompt": "Ricorda un momento in cui hai fatto qualcosa perche era giusto, anche se non era conveniente.", "instructions": "Cerca momenti in cui avresti potuto prendere una scorciatoia ma non l''hai fatto."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come i valori guidano le tue scelte", "prompt": "Cosa ti ha fatto scegliere l''integrita in quel momento?", "guiding_questions": ["Hai dovuto pensarci o e stato automatico?", "Cosa avresti perso scegliendo diversamente?", "Come ti sei sentito dopo?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare i valori in azione", "prompt": "Questa settimana, nota quando i tuoi valori guidano le tue scelte.", "action_steps": ["Osserva le decisioni che prendi", "Nota quando scegli in base a cosa e giusto", "Annota quali valori emergono piu spesso"]}'::jsonb, 'Lista di 3-5 decisioni guidate da valori', ARRAY['Quali valori guidano piu spesso le mie scelte?', 'Quando e difficile seguire i miei valori?', 'I miei valori dichiarati corrispondono a quelli in azione?'], 'I valori spesso operano in silenzio.');

-- Settimana 12: Creativita
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (12, 'Vedere Possibilita', 'Creativita', 'creativita', 'Vision', 'riconoscimento', 'base', 'Q1', 25, 'La creativita non e solo arte - e vedere possibilita dove altri vedono muri. Questa settimana osserverai quando questa capacita emerge nel tuo lavoro.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che vede possibilita", "prompt": "Ricorda un momento in cui hai trovato una soluzione non ovvia a un problema.", "instructions": "Cerca momenti in cui hai pensato: e se facessimo cosi?"}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come emerge la tua creativita", "prompt": "Come ti e venuta quell''idea?", "guiding_questions": ["Eri sotto pressione o rilassato?", "Eri solo o con altri?", "L''idea e arrivata subito o dopo tempo?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare il pensiero creativo", "prompt": "Questa settimana, nota quando pensi in modo non convenzionale.", "action_steps": ["Osserva quando ti viene un''idea nuova", "Nota le condizioni in cui succede", "Annota le idee, anche piccole"]}'::jsonb, 'Lista di 3-5 momenti di pensiero creativo', ARRAY['Quando sono piu creativo?', 'Cosa stimola la mia creativita?', 'Cosa la blocca?'], 'La creativita spesso passa inosservata perche la consideriamo normale.');

-- Settimana 13: Disciplina
INSERT INTO exercises_v2 (week_number, title, subtitle, characteristic_slug, pillar, exercise_type, difficulty_level, quarter, estimated_time_minutes, intro_validante, phase_1_recognition, phase_2_pattern, phase_3_expansion, deliverable, reflection_prompts, failure_response) VALUES (13, 'Energia Costante', 'Disciplina', 'disciplina', 'Action', 'riconoscimento', 'base', 'Q1', 25, 'La disciplina e la capacita di mantenere energia costante nel tempo. Questa settimana osserverai dove questa forza gia opera nella tua vita.', '{"title": "Riconoscimento - Chi Eri", "being_focus": "La persona che mantiene energia costante", "prompt": "Pensa a un''attivita che porti avanti con costanza, anche quando non ne hai voglia.", "instructions": "Cerca abitudini, routine, impegni che mantieni nel tempo."}'::jsonb, '{"title": "Pattern - Cosa Facevi", "doing_focus": "Come mantieni la disciplina", "prompt": "Cosa ti fa continuare anche quando non hai voglia?", "guiding_questions": ["E forza di volonta o abitudine?", "Cosa ti ricorda perche lo fai?", "Cosa succede quando salti?"]}'::jsonb, '{"title": "Espansione - Come Ricreare", "having_focus": "Notare la costanza", "prompt": "Questa settimana, nota dove mantieni energia costante.", "action_steps": ["Osserva le tue routine", "Nota cosa fai sempre, indipendentemente da come ti senti", "Annota cosa ti aiuta"]}'::jsonb, 'Lista di 3-5 aree di disciplina gia presenti', ARRAY['Dove la mia disciplina e piu forte?', 'Cosa mi aiuta a mantenere costanza?', 'Dove vorrei piu disciplina?'], 'La disciplina spesso la vediamo solo quando manca.');
